import React from "react";
import { Switch, Route, Link } from "react-router-dom";
import "./estilos/estiloPagina.css";
import Campeonato from "./componentes/campeonato";
import Home from "./componentes/home.js"

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        usuario: "",
        contraseña: ""
      },
      introUsuario: null,
    };
  }

  handleInputChange(e) {
    this.setState({introUsuario: {[e.name]: e.value}});
  }

  /*crea funciones de login y logout*/
  login() {
    this.setState({user: this.state.introUsuario});
  }

  logout() {
    this.setState({user: null});
  }

  render() {
    if (!this.state.user) {
      return (
        <div>
          <h1 style="text-align: center;">Campeonato Manager</h1>

          <div id="login">
            <form>
              <div id="usuario">
                <label>Usuario: </label>
                <input
                  type="text"
                  name="usuario"
                  value={this.state.user.usuario}
                  onChange={this.handleInputChange}
                ></input>
              </div>
              <div id="contraseña">
                <label>Contraseña: </label>
                <input
                  type="text"
                  name="contraseña"
                  value={this.state.user.contraseña}
                  onChange={this.handleInputChange}
                ></input>
              </div>
              <div id="boton-login">
                <button onClick={this.login}>Login</button>
              </div>
            </form>
          </div>
        </div>
      );
    } else {
      return (
        <div>
          <header>
            <h1>Campeonato Manager</h1>
          </header>
          <nav className="navbar navbar-expand navbar-dark " id="navbar">
            <div className="navbar-nav mr-auto">
              <li id="nav-item">
                <Link to={"/home"} className="nav-link">
                  Home
                </Link>
              </li>

              <li id="nav-item">
                <Link to={"/misDatos"} className="nav-link">
                  Mis Datos
                </Link>
              </li>

              <li id="nav-item">
                <a
                  onClick={this.logout}
                  className="nav-link"
                  style={{ cursor: "pointer" }}
                >
                  Logout {this.state.user.usuario}
                </a>
              </li>
            </div>
          </nav>

          <div className="container mt-3">
            <Switch>
              <Route
                path={["/", "/home"]}
                render={(props) => (
                  <Home {...props} user={this.state.user} />
                )}
              />
              {/* <Route
                path="/misDatos"
                render={(props) => (
                  <MisDatos {...props} user={this.state.user} />
                )}
              /> */}
              <Route
                path="/campeonato/:id"
                render={(props) => (
                  <Campeonato {...props} user={this.state.user} />
                )}
              />
            </Switch>
          </div>
        </div>
      );
    }
  }
}

export default App;
