function mostrarDatoPartido(dato) {
    let icono = "";
    if (dato.tipo === "Gol") {
        //icono = "far fa-futbol"
        icono = "../estilos/football.png"
    }
    else if (dato.tipo === "Amarilla") {
        icono = "../estilos/yellow.card.png"
    }
    else if (dato.tipo === "Roja") {
        icono = "../estilos/red-card.png"
    }
    return (
        <div class="datoPartido">
            <p><img src="estilos/yellow-card.png" alt="tipo"></img> '{dato.minuto} {dato.apellido}</p>
        </div>
    )
}

export default mostrarDatoPartido;
