import React, {useState, useEffect} from "react"
import { Switch, Route, Link } from "react-router-dom";

class edicionCampeonato extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            campeonato: props.campeonato,
            estado: props.campeonato.estado
        }
    }

    cambiarEstado() {
        fetch("http://localhost:8080/updateCampeonato",{method:"PUT", body: JSON.stringify(this.state.campeonato)}).then(
            response => {this.state.estado == "activo" ? (this.setState({estado: "inactivo"})) : this.setState({estado: "activo"})}
        ).catch(e => {console.log(e)})
    }


   render(){
       return( 
            <div>
                <h1>
                    {this.state.campeonato.nombre}
                    {this.state.estado}      <button onClick={() => {this.cambiarEstado()}}>{this.state.estado}</button>
                </h1>
            </div>
       )
   }

   //Si no anda, componentDidUpdate

}

export default edicionCampeonato;
