import React, { useState, useEffect } from "react";
import "../estilos/estiloPagina.css";
import filaTabla from "./filaTabla";
import ManagerDataService from "../servicios/misServicios"

class Campeonato extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      campeonato: props.campeonato,
      tabla: [],
      contador: 0,
      cargando: true,
    };
  }
  //id (int), descripcion (str), fechaInicio (Date), fechaFin (Date), estado (str), clubes (array de json)
  //clubes[i]: id (int), nombre (str), direccion (str)
  componentDidMount() {
    
    fetch("http://localhost:8080/getTablaPos?idCampeonato=" + this.state.campeonato.idCampeonato) //Busco resultados
      .then((response) => response.json())
      .then((tablasJSON) =>
        this.setState({ tabla: tablasJSON, cargando: false })
      )
      .catch((error) => {
        console.log(`Hay un error en la llamada: ${error}`);
      });
      
      // ManagerDataService.getTablaPos(this.state.campeonato.idCampeonato)
      // .then(response => {
      //   this.setState({ tabla: response.data, cargando: false })
      // })
      // .catch(e => {
      //   console.log(e)
      // })
  }

  incremento() {
    this.setState({ contador: this.state.contador + 1 });
    return this.state.contador;
  }

  render() {
    return (
      <div>
        <div class="titulo">{this.state.campeonato.descripcion}</div>
        <div id="tablaseccion">
          <div id="tablapts">
            <div class="titulotabla">
              Tabla {this.state.campeonato.descripcion}
            </div>
            <br />
            {/*Verificar si existen zonas. Si existen, iterar ?)*/}
            <table id="posiciones">
              <thead>
                {/*Head fijo*/}
                <tr class="cabecera">
                  <th>#</th>
                  <th>Equipo</th>
                  <th class="pts">Pts</th>
                  <th class="pj">PJ</th>
                  <th class="pg">PG</th>
                  <th class="pe">PE</th>
                  <th class="pp">PP</th>
                  <th class="gf">GF</th>
                  <th class="gc">GC</th>
                  <th class="dg">DIF</th>
                  <th class="prom">Prom</th>
                </tr>
              </thead>
              <tbody>
                {this.state.tabla.map((item) =>
                  filaTabla(item, this.state.campeonato, this.incremento())
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default Campeonato;
