import React, {useState, useEffect} from "react"
import { Switch, Route, Link } from "react-router-dom";

function EdicionCampeonato(campeonato){
    let estado = campeonato.estado

    function cambiarEstado() {
        fetch("http://localhost:8080/updateCampeonato",{method:"PUT", body: JSON.stringify(this.state.campeonato)}).then(
            response => {this.state.estado == "activo" ? (this.setState({estado: "inactivo"})) : this.setState({estado: "activo"})}
        ).catch(e => {console.log(e)})
    }

    return( 
        <div>
            <h1>
                {this.state.campeonato.nombre}
                {this.state.estado}      <button onClick={cambiarEstado}>{this.state.estado}</button>
            </h1>
        </div>
    )
    


}

export default EdicionCampeonato();
