import React, { useState, useEffect } from "react";
import "../estilos/estiloPagina.css";
import filaTabla from "./filaTabla";
import ManagerDataService from "../servicios/misServicios"

class Campeonato extends React.Component {
  constructor(props) {
    super(props);
    console.log("id: " + props.match.params.id)
    this.state = {
      idCampeonato: props.match.params.id,
      campeonato: null,
      tabla: [],
      contador: 0,
      cargando: true,
    };
  }
  //id (int), descripcion (str), fechaInicio (Date), fechaFin (Date), estado (str), clubes (array de json)
  //clubes[i]: id (int), nombre (str), direccion (str)

  componentDidMount(){
    fetch("http://localhost:8080/getCampeonato?idCampeonato=" + this.state.idCampeonato)
      .then((response) => response.json())
      .then((camp) => {
        console.log(camp)
        this.setState({campeonato: camp})
      })
      .catch((error) => {
        console.log(`Hay un error en la llamada: ${error}`);
      });

      fetch("http://localhost:8080/getTablaPos?idCampeonato=" + this.state.idCampeonato) //Busco resultados
      .then((response) => response.json())
      .then((tablasJSON) => {
        console.log(tablasJSON)
        let nuevaTabla = [];
        let indiceMax;
        let valMax;
        for(var i = 0; i < tablasJSON.length; i++){
          indiceMax = -1;
          valMax = -1;
          for(var j = 0; j < tablasJSON.length; j++){
            if (!(tablasJSON.at(j) in nuevaTabla) && (tablasJSON.at(j).at(8) > valMax)){
              indiceMax = j;
              valMax = tablasJSON.at(j).at(8);
            }
          }
          nuevaTabla.push(tablasJSON.at(indiceMax))
        }
        this.setState({ tabla: nuevaTabla, cargando: false })
      })
      .catch((error) => {
        console.log(`Hay un error en la llamada: ${error}`);
      });
  }

  incremento() {
    this.setState({ contador: this.state.contador + 1 });
    return this.state.contador;
  }

  render() {
    if (this.state.cargando) {
      return (
        <div>
          <h2>
            Cargando...
          </h2>
        </div>
      )
    }
    else {
    return (
      <div>
        <div class="titulo">{this.state.campeonato.descripcion}</div>
        <div id="tablaseccion">
          <div id="tablapts">
            <div class="titulotabla">
              Tabla {this.state.campeonato.descripcion}
            </div>
            <br />
            {/*Verificar si existen zonas. Si existen, iterar ?)*/}
            <table id="posiciones">
              <thead>
                {/*Head fijo*/}
                <tr class="cabecera">
                  <th>#</th>
                  <th>Equipo</th>
                  <th class="pts">Pts</th>
                  <th class="pj">PJ</th>
                  <th class="pg">PG</th>
                  <th class="pe">PE</th>
                  <th class="pp">PP</th>
                  <th class="gf">GF</th>
                  <th class="gc">GC</th>
                  <th class="dg">DIF</th>
                  <th class="prom">Prom</th>
                </tr>
              </thead>
              <tbody>
                {this.state.tabla.map((item, index) => 
                  filaTabla(item, this.state.campeonato, index + 1)
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
    }
  }
}

export default Campeonato;
